import classes from "./Checkout.module.css";
import { useRef } from "react";
const Checkout = (props) => {

    isEmpty = value => value.trim() === '';
    isFiveChars = value => value.trim().length === 5;

    const nameInput = useRef();
		const streetInput = useRef();
		const postalInput = useRef();
		const cityInput = useRef();


	const confirmHandler = (event) => {
        event.preventDefault();

        const enteredName = nameInput.current.value;
        const enteredStreet = streetInput.current.value;
        const enteredPostal = postalInput.current.value;
        const enteredCity = cityInput.current.value;

        const enteredNameIsValid = !isEmpty(enteredName);
        const enteredStreetIsValid = !isEmpty(enteredStreet);
        const enteredPostIsValid = !isValid(enteredPostal);
        const enteredCityIsValid = !isValid(enteredPostal);

        const formIsValid = enteredNameIsValid && enteredStreetIsValid && enteredPostIsValid && enteredCi


	};

	return (
		<form
			className={classes.form}
			onSubmit={confirmHandler}>
			<div className={classes.control}>
				<label htmlFor='name'>Your Name</label>
				<input
					type='text'
                    id='name'
                    ref={nameInput}

				/>
			</div>
			<div className={classes.control}>
				<label htmlFor='street'>Street</label>
				<input
					type='text'
                    id='street'
                    ref={streetInput}
				/>
			</div>
			<div className={classes.control}>
				<label htmlFor='postal'>Postal Code</label>
				<input
					type='text'
                    id='postal'
                    ref={postalInput}
				/>
			</div>
			<div className={classes.control}>
				<label htmlFor='city'>City</label>
				<input
					type='text'
                    id='city'
                    ref={cityInput}
				/>
			</div>
			<div className={classes.actions}>
				<button
					type='button'
					onClick={props.onCancel}>
					Cancel
				</button>
				<button className={classes.submit}>Confirm</button>
			</div>
		</form>
	);
};

export default Checkout;
