import Card from '../UI/Card';
import MealItem from './MealItem/MealItem';
import classes from './AvailableMeals.module.css';
import { useEffect, useState } from 'react';
import axios from 'axios';


const AvailableMeals = () => {

  const [mealsData, setMealsData] = useState([])

  // const getMeals = () => {
  //   axios
  //     .get("https://mealsapp-f67af-default-rtdb.firebaseio.com/meals.json")
  //   .then((res) => {
  //     const response = res.data;

  //     const newData = [];

  //     for (const mealNum in response) {
  //       newData.push(response[mealNum])
  //     }
  //     setMealsData(newData)
  //   })
  // };

  useEffect(() => {
    const innerFunc = async () => {
      const res = await axios.get("https://mealsapp-f67af-default-rtdb.firebaseio.com/meals.json")
      const response = res.data;
      const newData = [];
      for (const mealNum in response) {
        newData.push(response[mealNum]);
      }
      setMealsData(newData);
    }
    innerFunc();
},[])

  console.log(mealsData)

  const mealsList = mealsData.map((mealObj) => (
    <MealItem
      key={mealObj.id}
      id={mealObj.id}
      name={mealObj.name}
      description={mealObj.description}
      price={mealObj.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
